#defaultView:Tree
SELECT
  ?P277
  ?P277Label
  ?P31
  ?P31Label
  ?software
  ?softwareLabel
WITH {
  SELECT DISTINCT ?license {
    ?license wdt:P629*/wdt:P31/wdt:P279* wd:Q3943414.
  }
} AS %0
WITH {
  SELECT DISTINCT ?software {
    INCLUDE %0.
    # "GNU Emacs" (Q1252773) has copyright license "GPLv3+" (Q27016754)
    # "GPLv3+" (Q27016754) is en edition or translation of "GNU GPL" (Q7603)
    # "GNU GPL" (Q7603) is an instance of "free software license" (Q3943414)
    {?software wdt:P275 ?license}
    UNION
    # "Vim" is an instance of "free and open-source software" (Q506883)
    # "free and open-source software" (Q506883) is a subclass of "free software" (Q341)
    {?software wdt:P31/wdt:P279* wd:Q341}.
  }
} AS %1
WITH {
  SELECT DISTINCT ?software ?P31 ?P277 {
    INCLUDE %1.
    ?software
      wdt:P31 ?P31;
      wdt:P277 ?P277.
  }
} AS %2
{
  INCLUDE %2.
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE]"}
}
ORDER BY
  ASC(LCASE(?P277Label))
  ASC(LCASE(?P31Label))

