#title: German nouns with unparseable combines statements
select *
with {
  select distinct ?l {
    ?l dct:language wd:Q188;
       wikibase:lexicalCategory wd:Q1084;
       wikibase:lemma ?lemma;
       wdt:P5238 ?comb1;
       wdt:P5238 ?comb2 filter (?comb1 != ?comb2).

    ?comb1 wikibase:lemma ?combl1.
    optional {
      ?comb1 ontolex:lexicalForm [
        wikibase:grammaticalFeature wd:Q107614077;
        ontolex:representation ?combf1
      ]
    }

    ?comb2 wikibase:lemma ?combl2.

    bind(concat(replace(coalesce(?combf1, ?combl1), "-$", ""), lcase(replace(?combl2, "^-", ""))) as ?genlemma).
    filter(?lemma = ?genlemma).
  }
} as %ok
where {
  ?l dct:language wd:Q188;
     wikibase:lexicalCategory wd:Q1084;
     wikibase:lemma ?lemma;
     wdt:P5238 ?comb.
  ?comb wikibase:lemma ?combl.
  minus { include %ok }
} order by ?lemma ?comb

#title: Grammatical features used on English nouns and proper nouns
select ?feat ?en (count(*) as ?total)
with {
  select * {
    values ?lexcat { wd:Q1084 wd:Q147276 }
    ?l dct:language wd:Q1860;
       wikibase:lexicalCategory ?lexcat;
       ontolex:lexicalForm ?f.
    ?f wikibase:grammaticalFeature ?feat.
  }
} as %feats
{
  include %feats.
  ?feat rdfs:label ?en filter (lang(?en) = "en").
} group by ?feat ?en order by desc(?total) ?en

