  ?item wdt:P5008 ?f . hint:Prior hint:runFirst true.
  ?item wdt:P5008 wd:Q111645234 .  
  ?item wdt:P31 wd:Q5 .
  } group by ?item ?itemLabel having (?count>1) } as %i
WHERE
{
  INCLUDE %i
  ?item wdt:P5008 ?project .
  filter (?project not in (wd:Q111645234 ))
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". 
                           ?item rdfs:label ?itemLabel .
                           ?item schema:description ?itemDescription .
                           ?project rdfs:label ?projectLabel . } 
} group by ?project ?projectLabel order by desc(?items)

