#title:Holdings of Arabic periodicals
#defaultView:Map{"hide":["?coords"], "markercluster":"true"}
SELECT DISTINCT
  (URI(str(?periodical)) as ?item) # link to the wikidata item
  (CONCAT(?periodicalLabel, ": ", ?periodicalDesc) as ?itemLabel) # construct a label for the item
  ?coords
  ?collectionLabel
WITH {
  SELECT DISTINCT
    ?periodical ?dateOnset
  WHERE {
   ?periodical wdt:P31/wdt:P279* wd:Q1002697; # get all periodicals
       wdt:P407/wdt:P279* wd:Q13955.          # limit publication language to Arabic
    # limit to titles published before 1930
    OPTIONAL {?periodical (wdt:P571 | wdt:P580) ?dateOnset}.
    FILTER( YEAR(?dateOnset) < 1930).
    }
  LIMIT 4000
  } as %periodicals
WITH {
  SELECT DISTINCT
   ?periodical ?dateOnset ?collection
  WHERE {
    INCLUDE %periodicals
    ?periodical wdt:P195 ?collection .
  }
  LIMIT 10000
} as %holdings
WHERE {
  INCLUDE %holdings
  OPTIONAL{ ?collection wdt:P625 ?coords .}
  SERVICE wikibase:label {
      bd:serviceParam wikibase:language "[AUTO_LANGUAGE], ar".
      ?periodical rdfs:label ?periodicalLabel;
          schema:description ?periodicalDesc.
      ?collection rdfs:label ?collectionLabel.
  }
}
ORDER BY ?dateOnset
LIMIT 10000

