 ( coalesce(?coordonnées_géographiques,?coord2)  as ?coords)
 WHERE {
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],mul,en". }
  ?essai (wdt:P31/(wdt:P279*)) wd:Q210112;
    wdt:P137 wd:Q142;
