# Statistics for lexemes in language A, derived from lexeme (P5191) in language B
select ?lang_a ?lang_aLabel ?sample_a ?lang_b ?lang_bLabel ?count_a ?sum_a ?pct
with {
  select ?lang_a ?lang_b (sample(?lex_a) as ?sample_a) (count(?lex_a) as ?count_a)
  where {
    # set ?lang_a equal to the languages we want to do statistics over
    { select distinct ?lang_a where {
      ?lang_a wdt:P279* wd:Q33577 . # ?lang_a is subclass of indo-aryan
    } }
    #values ?lang_a { wd:Q1860 } # english. for testing

    ?lex_a dct:language ?lang_a .
    optional {
      ?lex_a wdt:P5191 ?lex_b .
      ?lex_b dct:language ?lang_b .
    }
  }
  group by ?lang_a ?lang_b
} as %raw
with {
  select ?lang_a (sum(?count_a) as ?sum_a) where {
    include %raw
  }
  group by ?lang_a
} as %total
where {
  {
    include %raw
    include %total
    bind(round(?count_a * 10000 / ?sum_a) / 100 as ?pct)
  }
  service wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],mul,en" . }
}
order by ?lang_a desc(?count_a)

