#title: Objects in Sri Lanka, ( Bridges, Dams, Rivers, Structures, Forests, Waterfalls, Power Stations, Mountains, Water bodies, Train stations, National parks, and Buildings )
#defaultView:Map,Table
SELECT distinct ?item ?itemLabel ?itemDescription ?regionLabel ?image ?coord ?rgb
WHERE {
  ?item wdt:P17 wd:Q854.  # in Sri Lanka
  VALUES ?type { 
    wd:Q12280  wd:Q12323 wd:Q4022 wd:Q811979 wd:Q4421 wd:Q34038 wd:Q159719 wd:Q8502 wd:Q15324 wd:Q55488 wd:Q46169 wd:Q41176
  }
  ?item wdt:P31/wdt:P279* ?type.
  OPTIONAL { ?item wdt:P625 ?coord }  
  OPTIONAL { ?item wdt:P18 ?image } 
  OPTIONAL { ?item wdt:P131 ?region }
  BIND(IF(BOUND(?image),"00A62C","C90202") AS ?rgb)
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],mul,en". }
}

SELECT DISTINCT ?item WHERE {
  ?item wikibase:statements 0 .
  MINUS { ?item dct:language [] } . # exclude lexemes
  MINUS { [] schema:about ?item; schema:isPartOf [] } # exclude items with no Wikipedia, etc. article
}

#title: Showcase lexemes and their language/lexical category
select *
with {
  select * {

    SERVICE wikibase:mwapi {
      bd:serviceParam wikibase:api "Generator";
                      wikibase:endpoint "www.wikidata.org";
                      mwapi:gcmtitle ?title;
                      mwapi:generator "links";
                      mwapi:gpllimit "max";
                      mwapi:gplnamespace "146";
                      mwapi:titles "Wikidata:Showcase lexemes".
      ?title wikibase:apiOutput mwapi:title.
    }
  }
} as %data
{
  include %data.
  bind(uri(replace(?title, "Lexeme:", str(wd:))) as ?l).
  ?l dct:language ?lang;
     wikibase:lexicalCategory ?lexcat;
     wikibase:lemma ?lemma.
} order by str(?lemma)

# Obscure units of measurement and where to find them.
# This version removes unconnected claims, but won't be able to get samples for all the units.
# ?count is number of value nodes which might be unconnected or reused.
# ?truecount is number of claims referencing this value node.
# By: User:Infrastruktur
select ?unit ?unitLabel ?count ?truecount ?sample ?uses
with {
  select ?unit (count(?vn) as ?count) where {
    ?vn wikibase:quantityUnit ?unit.
  } group by ?unit
} as %units
with {
  select ?unit ?count (count(distinct ?st) as ?truecount) (sample(?st) as ?sample) where {
    include %units
    filter(?count < 5)
    _:vn wikibase:quantityUnit ?unit .
    ?st ?p _:vn .
    [] wikibase:statementValue|wikibase:statementValueNormalized ?p .
  } group by ?unit ?count
} as %samples
where {
  include %samples
  bind(uri(concat("https://query.wikidata.org/#select%20%3Fst%20where%20%7B%0A%20%20_%3Avn%20wikibase%3AquantityUnit%20wd%3A", substr(str(?unit), 32), "%20.%0A%20%20%3Fst%20%3Fp%20_%3Avn%20.%0A%20%20%5B%5D%20wikibase%3AstatementValue%7Cwikibase%3AstatementValueNormalized%20%3Fp%20.%0A%7D")) as ?uses)
  service wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],mul,en" . }
}
order by ?count

