#title:oldest known individual per taxon (pre-20th century)
SELECT ?individual ?individualLabel ?dateOfBirth (SAMPLE(?image_) AS ?image) ?taxon ?taxonLabel WITH {
  SELECT ?taxon (MIN(?dateOfBirth_) AS ?dateOfBirth) WHERE {
    ?individual wdt:P10241 ?taxon;
                wdt:P569 ?dateOfBirth_. hint:Prior hint:rangeSafe true.
    FILTER(!wikibase:isSomeValue(?dateOfBirth_))
    FILTER(?dateOfBirth_ < "1901-00-00"^^xsd:dateTime) # arbitrary cutoff, past this point results don’t seem all that interesting anymore
  }
  GROUP BY ?taxon
} AS %taxons WITH {
  SELECT ?taxon ?dateOfBirth (SAMPLE(?individual_) AS ?individual) WHERE {
    INCLUDE %taxons.
    ?individual_ wdt:P10241 ?taxon;
                 wdt:P569 ?dateOfBirth.
  }
  GROUP BY ?taxon ?dateOfBirth
} AS %results WHERE {
  INCLUDE %results.
  OPTIONAL { ?individual wdt:P18 ?image_. }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],mul,en". }
}
GROUP BY ?individual ?individualLabel ?dateOfBirth ?taxon ?taxonLabel
ORDER BY ?dateOfBirth

SELECT ?item ?itemLabel ?_articleEN ?_articlePT ?badge_label_PT ?_articleES ?badge_label_ES WHERE {
  SERVICE wikibase:mwapi {
    bd:serviceParam wikibase:endpoint "en.wikipedia.org" .
    bd:serviceParam wikibase:api "Generator" .
    bd:serviceParam mwapi:gcmtitle "Category:Featured articles" .
    bd:serviceParam mwapi:generator "categorymembers" .
    bd:serviceParam mwapi:gcmtype "page" .
    bd:serviceParam mwapi:gcmlimit "max" .
    ?item wikibase:apiOutputItem mwapi:item .
    ?itemLabel wikibase:apiOutput mwapi:title .
  }
  OPTIONAL {
    ?_articleEN schema:about ?item;
                schema:isPartOf <https://en.wikipedia.org/>. }
  OPTIONAL {
    ?_articlePT schema:about ?item;
                schema:isPartOf <https://pt.wikipedia.org/>;
                wikibase:badge ?badgePT. }
  OPTIONAL {
    ?_articleES schema:about ?item;
                schema:isPartOf <https://es.wikipedia.org/>;
                wikibase:badge ?badgeES. }
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "mul,en" .
    ?badgePT rdfs:label ?badge_label_PT . ?badgeES rdfs:label ?badge_label_ES .
  }
}

SELECT ?item ?itemLabelEn ?itemDescEn ?itemLabelFr ?class
WHERE {
  {
    ?class wdt:P279* wd:Q756.  # ?class must be a subclass of plants
    ?item wdt:P31 ?class.      # and ?item must be an instance of ?class
  }
  UNION                        # or...
  {
    ?item wdt:P279* wd:Q756.   # ?item itself must be a subclass of plants
  }

  # get label and description in French
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "fr" .
    ?item rdfs:label ?itemLabelFr .
    ?item schema:description ?itemDescFr .
  }
  FILTER(!bound(?itemDescFr))  # filter out entries with a French description set

  # get label and description in English (optional, remove this section and "?itemLabelEn ?itemDescEn" in the selectors if you don't need it)
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en" .
    ?item rdfs:label ?itemLabelEn .
    ?item schema:description ?itemDescEn .
  }
}
LIMIT 10                       # and return at most 10 results

SELECT ?parentOrganization ?parentOrganizationLabel (GROUP_CONCAT(?commercialOrganization_Label; SEPARATOR = ", ") AS ?commercialOrganizations) (COUNT(*) AS ?numberOfChildren) WHERE {
  ?commercialOrganization (wdt:P31/(wdt:P279*)) wd:Q21980538;
    wdt:P749 ?parentOrganization.
  ?commercialOrganization rdfs:label ?commercialOrganization_Label. FILTER(LANG(?commercialOrganization_Label) = "en")
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],mul,en". }
}
GROUP BY ?parentOrganization ?parentOrganizationLabel
ORDER BY DESC(?numberOfChildren)

SELECT ?sample ?name ?count
WHERE {
  { SELECT ?fname (sample(?item_) as ?sample) (count(?item_) as ?count) where {
    ?item_ wdt:P31 wd:Q5;
      wdt:P27 wd:Q183;
      wdt:P734 ?fname.
  } GROUP BY ?fname }
  OPTIONAL { ?fname wdt:P1705 ?nameDE. FILTER(LANG(?nameDE)='de') }
  OPTIONAL { ?fname wdt:P1705 ?nameMUL. FILTER(LANG(?nameMUL)='mul') }
  BIND(COALESCE(?nameDE, ?nameMUL) AS ?name)
}
order by desc(?count)

