PREFIX target: <http://www.wikidata.org/entity/Q18507561>

# List of related works by co-citation analysis
SELECT ?count ?work ?workLabel
WITH {
  SELECT ?work (COUNT(?work) AS ?count)
  WHERE {
    target: (^wdt:P2860 | wdt:P2860) / (^wdt:P2860 | wdt:P2860)? ?work .
    FILTER (target: != ?work)
  }
  GROUP BY ?work
  LIMIT 500
} AS %result
WHERE {
  INCLUDE %result
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],mul,en" . } 
} 
ORDER BY DESC(?count)

SELECT ?seriesNumber ?album ?albumLabel ?albumDescription  ?date ?scenariste ?dessinateur?coloriste  (GROUP_CONCAT(DISTINCT ?personnagesLabel ; SEPARATOR = ", ") AS ?personnages) WHERE {
  ?album p:P179 ?seriesStmt; wdt:P58 ?scenarist;  wdt:P10837 ?dessinateu;wdt:P577 ?date; wdt:P6338 ?colorist  .
  ?seriesStmt ps:P179 wd:Q1030833 ;   
    pq:P1545 ?seriesNumber.     
  OPTIONAL { ?album wdt:P674 ?personnages.  }     
  SERVICE wikibase:label {    
    bd:serviceParam wikibase:language "fr".   
    ?album rdfs:label ?albumLabel;schema:description ?albumDescription.   
           ?colorist rdfs:label ?coloriste.
           ?scenarist rdfs:label ?scenariste.  
    ?dessinateu rdfs:label ?dessinateur . 
    ?personnages rdfs:label ?personnagesLabel.
  }
} 
GROUP BY ?album ?albumLabel ?seriesNumber ?albumDescription ?date ?scenariste ?coloriste ?dessinateur
ORDER BY (xsd:integer(?seriesNumber) )

#defaultView:Map
SELECT DISTINCT (?whattype as ?layer) ?itemLabel ?item ?PubID ?img ?web ?loc  WHERE { 
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
  ?item wdt:P13516 ?pubID.
  OPTIONAL {?item wdt:P31 ?thisType. ?thisType rdfs:label ?whattype filter (lang(?whattype) = "en"). }
  VALUES ?scotmarks {wd:Q22 wd:Q15060255 }.
  {?item wdt:P131 ?scotmarks.} 
    UNION {?item wdt:P131 ?orgloc1. ?orgloc1 wdt:P31/wdt:P131 ?scotmarks.} 
    UNION {?item wdt:P159 ?orgpl. {{?orgpl wdt:P31/wdt:P131 ?scotmarks.} .} 
    UNION {?orgpl wdt:P131 ?orgl2. {{?orgl2 wdt:P31/wdt:P131 ?scotmarks} }}}.
  optional {?item wdt:P18 ?img.}.
  ?item wdt:P625 ?loc.
  OPTIONAL {?item wdt:P856 ?web}.
  BIND(URI(CONCAT("https://camra.org.uk/pubs/", ?pubID)) AS ?PubID).
}

