#title:Exemplars of the Magna Carta
SELECT ?item ?itemLabel (YEAR(?date) AS ?year) ?w ?h ?url WHERE {
  ?item wdt:P1574 wd:Q12519
  OPTIONAL {?item wdt:P571 ?date}
  OPTIONAL {?item wdt:P973 ?url}
  OPTIONAL {?item wdt:P2049 ?w}
  OPTIONAL {?item wdt:P2048 ?h}
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en,mul". }
} ORDER BY ?date ?itemLabel

SELECT ?l ?lemma (xsd:integer(STRAFTER(STR(?l), "L")) AS ?numericId) WHERE {
  ?l a ontolex:LexicalEntry ;
     dct:language wd:Q13324 ;
     wikibase:lemma ?lemma .
}
ORDER BY ?numericId

#title: film directors who are alive 
#defaultView:Table;ImageGrid
SELECT ?item ?itemLabel ?itemDescription ?date_of_birth 
WHERE {
  #hint:Query hint:optimizer "None" . 
  ?item wdt:P31 wd:Q5.
  ?item wdt:P106 wd:Q2526255.
  OPTIONAL { ?item wdt:P569 ?date_of_birth. } 
  MINUS {?item wdt:P570 ?date_of_death. } 
  MINUS {?item wdt:P20 ?place_of_death. } 
  #FILTER (?date_of_birth >= "1870-01-01T00:00:00Z"^^xsd:dateTime)   # born after 
  #FILTER (?date_of_birth <= "1900-07-01T00:00:00Z"^^xsd:dateTime)  # born before
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],mul,en". }
}
#order by ?date_of_birth
LIMIT 100

